mapper.select:
SELECT *
FROM enrollments
WHERE student_id = #{studentId}
And course_id = #{courseId}

mapper.insert:
INSERT INTO enrollments
(
student_id,
course_id
)
VALUES
(
#{studentId},
#{courseId}
)

mapper.delete:
DELETE
FROM enrollments
WHERE student_id = #{studentId}
AND course_id = #{courseId}
